#include "bits/stdc++.h"using namespace std;const int MAXN = 2e5 + 1;int n, m, x, l;vector<int> adj[MAXN];int rela[MAXN];int main() {    ios::sync_with_stdio(0);    cin.tie(0);    cin >> n >> m >> x >> l;    for (int i = 0; i < m; i++) {        int a, b;        cin >> a >> b;        rela[a]++;        rela[b]++;        adj[a].push_back(b);        adj[b].push_back(a);    }    vector<bool> vis(n + 1, false);    vector<int> gone(n + 1, 0);    queue<int> q;    q.push(l);    vis[l] = true;    while (!q.empty()) {        int v = q.front();        q.pop();        for (auto u : adj[v]) {            if (vis[u]) continue;            if ((++gone[u]) * 2 < rela[u]) continue;            vis[u] = true;            q.push(u);        }    }    if (vis[x])        cout << "leave\n";    else        cout << "stay\n";}